import React from "react";
import { Text, StyleSheet, View, Button, TouchableOpacity, Image,S } from "react-native";



const SemesterScreen = props => {

    this.state = {
        firstSemesterCourse : [
            { course_id: 'SWE 4101' },
            { course_id: 'CSE 4105' },
            { course_id: 'Math 4103' },
        ]
    };

    return (
        <View style={styles.viewStyle}>
            <Button
                title="First Semester"
                onPress={() => { props.navigation.navigate('Course', { data: this.state.firstSemesterCourse }) }} />
            <Button
                title="Second Semester"
                onPress={() => { }} />
            <Button
                title="Third Semester"
                onPress={() => { }} />
        </View>
    );
}

const styles = StyleSheet.create({
    viewStyle: {
        flex: 2,
        flexDirection: 'column',
        justifyContent: 'space-evenly',
        alignItems: 'center'
    }
});

export default SemesterScreen;
