import React from "react";
import { Text, StyleSheet, View, Button, TouchableOpacity, Image } from "react-native";
import { color } from "react-native-reanimated";

const HomeScreen = props => {
    return (
        <View style={styles.view}>
            <Image
                style={styles.logo}
                source={require('../../assets/IUT.png')}
            />

            <Text style={styles.headingText}>Depatment of CSE</Text>
            <Text style={styles.headingText}>Programme: SWE</Text>

            <TouchableOpacity
                onPress={() => props.navigation.navigate('Profile')}
                style={styles.appButtonContainer}>
                <Text style={{ fontSize: 27, margin: 5 }}>My Profile</Text>
            </TouchableOpacity>
            <Button title="Semester Wise Course List"/>
            <Button title="List of Faculty Members"/>

        </View>
    );
}

const styles = StyleSheet.create({
    headingText: {
        fontSize: 30,
        fontWeight: "bold",
        margin: 15,
    },
    logo: {
        width: 250,
        height: 250,
        resizeMode: 'contain'
    },
    view: {
        flax:3,
        flexDirection:3,
        borderWidth: 0,
        alignItems: 'center',
    },
    buttonView: {
        flex: 1,
        justifyContent: "space-evenly",
    },
    appButtonContainer: {
        elevation: 8,
        backgroundColor: "#D8F3FF",
        borderRadius: 35,
        paddingVertical: 5,
        paddingHorizontal: 25
    }
});

export default HomeScreen;
